import React, { useState } from "react";
import UserInfo from "./UserInfo";

const CoursesLeft = ({ coursesLeft = [] }) => {
  const [userName] = useState("John Doe");
  const [graduationDate] = useState("May 2026");
  const [gpa] = useState("3.8");
  const [degreeProgress] = useState("75");
  const [major] = useState("Computer Science");
  const [degreeType] = useState("Bachelor of Science");

  const [selectedTerms, setSelectedTerms] = useState(
    Array.isArray(coursesLeft)
      ? coursesLeft.reduce((acc, category) => {
          category.courses.forEach((course) => {
            acc[course.name] = {
              term: course.term || "",
              notes: course.notes || "",
              option: course.options ? course.selectedOption || "" : "",
            };
          });
          return acc;
        }, {})
      : {}
  );

  const [isModalOpen, setIsModalOpen] = useState(false);
  const [isSubmitted, setIsSubmitted] = useState(false);

  const handleTermChange = (courseName, term) => {
    setSelectedTerms((prev) => ({
      ...prev,
      [courseName]: { ...prev[courseName], term },
    }));
  };

  const handleNotesChange = (courseName, notes) => {
    setSelectedTerms((prev) => ({
      ...prev,
      [courseName]: { ...prev[courseName], notes },
    }));
  };

  const handleOptionChange = (courseName, option) => {
    setSelectedTerms((prev) => ({
      ...prev,
      [courseName]: { ...prev[courseName], option },
    }));
  };

  const saveDegreePlanToFile = () => {
    const degreePlan = {
      userName,
      graduationDate,
      gpa,
      degreeProgress,
      major,
      degreeType,
      selectedTerms,
    };

    const blob = new Blob([JSON.stringify(degreePlan, null, 2)], { type: "application/json" });
    const url = URL.createObjectURL(blob);

    const link = document.createElement("a");
    link.href = url;
    link.download = "degree_plan.json";
    link.click();

    URL.revokeObjectURL(url);
  };

  const handleSubmit = () => {
    setIsModalOpen(false);
    setIsSubmitted(true);
    saveDegreePlanToFile();
  };

  return (
    <div style={appContainerStyles}>
      {isSubmitted ? (
        <div style={thankYouContainerStyles}>
          <h1>Congratulations! You submitted your degree planner!</h1>
          <p>Thanks and Gig 'em!</p>
          <img
            src="https://upload.wikimedia.org/wikipedia/commons/thumb/1/1b/Reveille_Aggies.jpg/1200px-Reveille_Aggies.jpg"
            alt="Reveille"
            style={imageStyles}
          />
        </div>
      ) : (
        <>
          {/* Header */}
          <div style={headerStyles}>Texas A&M Degree Plan</div>

          {/* User Info */}
          <UserInfo
            userName={userName}
            graduationDate={graduationDate}
            gpa={gpa}
            degreeProgress={degreeProgress}
            major={major}
            degreeType={degreeType}
          />

          {/* Main Content */}
          <div style={containerStyles}>
            {coursesLeft.map((category) => (
              <div key={category.category} style={categoryCardStyles}>
                <h2 style={categoryTitleStyles}>{category.category}</h2>
                {category.courses.map((course) => (
                  <div key={course.name} style={courseRowStyles}>
                    <span style={courseNameStyles}>
                      {course.name} {course.options && "(Elective)"}
                    </span>
                    <div style={selectGroupStyles}>
                      {course.options ? (
                        <select
                          value={selectedTerms[course.name]?.option || ""}
                          onChange={(e) => handleOptionChange(course.name, e.target.value)}
                          style={selectStyles}
                        >
                          <option value="">Select Course</option>
                          {course.options.map((option) => (
                            <option key={option} value={option}>
                              {option}
                            </option>
                          ))}
                        </select>
                      ) : null}
                      <select
                        value={selectedTerms[course.name]?.term || ""}
                        onChange={(e) => handleTermChange(course.name, e.target.value)}
                        style={selectStyles}
                      >
                        <option value="">Select Term</option>
                        <option value="Fall 2024">Fall 2024</option>
                        <option value="Spring 2025">Spring 2025</option>
                        <option value="Fall 2025">Fall 2025</option>
                        <option value="Spring 2026">Spring 2026</option>
                      </select>
                      <input
                        type="text"
                        placeholder="Add notes here"
                        value={selectedTerms[course.name]?.notes || ""}
                        onChange={(e) => handleNotesChange(course.name, e.target.value)}
                        style={notesInputStyles}
                      />
                    </div>
                  </div>
                ))}
              </div>
            ))}
          </div>

          {/* Submit Button */}
          <button
            onClick={() => setIsModalOpen(true)}
            style={submitButtonStyles}
          >
            Submit
          </button>

          {/* Confirmation Modal */}
          {isModalOpen && (
            <div style={modalStyles}>
              <div style={modalContentStyles}>
                <h2>Are you sure you want to submit your degree plan?</h2>
                <button style={confirmButtonStyles} onClick={handleSubmit}>
                  Yes, Submit
                </button>
                <button style={cancelButtonStyles} onClick={() => setIsModalOpen(false)}>
                  Cancel
                </button>
              </div>
            </div>
          )}
        </>
      )}
    </div>
  );
};

// Add the new and updated styles here
// Updated styles omitted for brevity but are as described in the earlier response.

const headerStyles = {
    backgroundColor: "#A60F2D", // Bright maroon for header
    color: "#FFFFFF", // White text for strong contrast
    textAlign: "center",
    padding: "25px",
    fontSize: "32px",
    fontWeight: "bold",
    borderRadius: "10px",
    marginBottom: "20px",
    boxShadow: "0 6px 15px rgba(0, 0, 0, 0.2)", // More prominent shadow
  };



export default CoursesLeft;
