import axios from "axios";
import React, { useState } from "react";

const Chatbot = () => {
  const [messages, setMessages] = useState([]);
  const [userInput, setUserInput] = useState("");

  const handleSendMessage = async () => {
    if (!userInput.trim()) return;

    // Add user message to the chat
    setMessages((prevMessages) => [...prevMessages, { role: "user", content: userInput }]);

    try {
      const response = await axios.post("http://localhost:5000/api/chatbot", {
        message: userInput,
      });
      const botReply = response.data.reply;

      // Add bot reply to the chat
      setMessages((prevMessages) => [...prevMessages, { role: "bot", content: botReply }]);
    } catch (error) {
      console.error("Error communicating with the chatbot:", error);
      setMessages((prevMessages) => [
        ...prevMessages,
        { role: "bot", content: "An error occurred while communicating with the chatbot." },
      ]);
    }

    setUserInput("");
  };

  return (
    <div>
      {/* Chat Display */}
      <div>
        {messages.map((msg, index) => (
          <div key={index}>
            <strong>{msg.role === "user" ? "You" : "Bot"}:</strong> {msg.content}
          </div>
        ))}
      </div>

      {/* Input Box */}
      <input
        type="text"
        value={userInput}
        onChange={(e) => setUserInput(e.target.value)}
        placeholder="Type a message..."
      />
      <button onClick={handleSendMessage}>Send</button>
    </div>
  );
};



// Styles
const chatbotContainerStyles = {
  position: "fixed",
  bottom: "20px",
  right: "20px", // Change to right-hand side
  zIndex: 1000,
};

const chatboxStyles = {
  width: "300px",
  height: "400px",
  backgroundColor: "#FFFFFF",
  border: "1px solid #ccc",
  borderRadius: "10px",
  boxShadow: "0 4px 8px rgba(0, 0, 0, 0.2)",
  display: "flex",
  flexDirection: "column",
};

const chatboxHeaderStyles = {
  backgroundColor: "#7B0000", // Texas A&M maroon
  color: "#fff",
  padding: "10px",
  borderTopLeftRadius: "10px",
  borderTopRightRadius: "10px",
  display: "flex",
  justifyContent: "space-between",
  alignItems: "center",
};

const closeButtonStyles = {
  backgroundColor: "transparent",
  color: "#fff",
  border: "none",
  fontSize: "16px",
  cursor: "pointer",
};

const chatboxBodyStyles = {
  flex: 1,
  padding: "10px",
  overflowY: "auto", // Scrollable content
};

const chatboxFooterStyles = {
  display: "flex",
  padding: "10px",
  borderTop: "1px solid #ccc",
};

const inputStyles = {
  flex: 1,
  padding: "8px",
  borderRadius: "5px",
  border: "1px solid #ccc",
  marginRight: "10px",
};

const sendButtonStyles = {
  backgroundColor: "#7B0000",
  color: "#fff",
  border: "none",
  borderRadius: "5px",
  padding: "8px 12px",
  cursor: "pointer",
};

const chatButtonStyles = {
  backgroundColor: "#7B0000", // Texas A&M maroon
  color: "#fff",
  border: "none",
  borderRadius: "50%",
  width: "60px",
  height: "60px",
  fontSize: "24px",
  cursor: "pointer",
  boxShadow: "0 4px 8px rgba(0, 0, 0, 0.2)",
};

const botMessageStyles = {
  backgroundColor: "#f1f0f0",
  color: "#000",
  padding: "8px",
  borderRadius: "10px",
  marginBottom: "8px",
  alignSelf: "flex-start",
};

const userMessageStyles = {
  backgroundColor: "#7B0000",
  color: "#fff",
  padding: "8px",
  borderRadius: "10px",
  marginBottom: "8px",
  alignSelf: "flex-end",
};

export default Chatbot;
