import React, { useState } from "react";
import { BrowserRouter as Router, Routes, Route, Link } from "react-router-dom";
import Header from "./components/header";
import ProgressBar from "./components/progressBar";
import CourseList from "./components/courseList";
import Footer from "./components/footer";
import AddClasses from "./components/addClasses";

const App = () => {
  const [courses, setCourses] = useState({
    "Fall 2024": ["Math 101", "History 201", "Biology 150"],
    "Spring 2025": ["CS 101", "Physics 110", "Art 100"],
  });

  return (
    <Router>
      <div style={appStyles}>
        <Header studentName="John Doe" />
        <nav style={navStyles}>
          <Link to="/" style={linkStyles}>Home</Link>
          <Link to="/add-classes" style={linkStyles}>Add Classes</Link>
        </nav>
        <Routes>
          <Route
            path="/"
            element={
              <>
                <ProgressBar progress={75} />
                {Object.entries(courses).map(([term, courseList]) => (
                  <CourseList key={term} term={term} courses={courseList} />
                ))}
              </>
            }
          />
          <Route
            path="/add-classes"
            element={<AddClasses courses={courses} setCourses={setCourses} />}
          />
        </Routes>
        <Footer />
      </div>
    </Router>
  );
};

const navStyles = {
  marginBottom: "20px",
  textAlign: "center",
};

const linkStyles = {
  margin: "0 10px",
  textDecoration: "none",
  color: "#007BFF",
};

const appStyles = {
  fontFamily: "Arial, sans-serif",
  maxWidth: "600px",
  margin: "0 auto",
  padding: "20px",
};

export default App;
