const courseRowStyles = {
  display: "flex",
  flexDirection: "row", // Arrange items in a row
  flexWrap: "wrap", // Ensure items wrap within their container
  gap: "12px",
  marginBottom: "15px",
  alignItems: "flex-start", // Align inputs to the top
};


const courseNameStyles = {
