  color: "#333333",
  marginBottom: "5px",
};

const selectGroupStyles = {
  display: "flex",
  flexDirection: "row",
  gap: "10px",
  alignItems: "center",
};

const selectStyles = {
  flex: 1,
  padding: "8px",
  borderRadius: "6px",
  border: "1px solid #CCCCCC",
  backgroundColor: "#FFFFFF",
  color: "#333333",
  minWidth: "160px",
};

const textInputStyles = {
  flex: 1,
  padding: "8px",
