import React, { useState } from "react";

// Options for electives based on their respective categories
const electiveOptions = {
  "Science Elective": ["BIOL 101", "CHEM 101", "PHYS 201", "GEOL 101"],
  "Cultural and Intercultural Discourse": ["ICD 101", "ICD 102", "ICD 103", "ICD 104"],
  "Language, Philosophy, and Culture": ["PHIL 111", "ENGL 212", "HIST 230", "CLAS 102"],
  "Creative Arts": ["ARTS 150", "MUSC 200", "THAR 101", "ARCH 249"],
  "Social and Behavioral Sciences": ["PSYC 107", "SOCI 205", "ECON 202", "ECON 203"],
  History: ["HIST 105", "HIST 106", "HIST 230", "HIST 258"],
};

const CoursesLeft = ({ coursesLeft = [] }) => {
  const [selectedTerms, setSelectedTerms] = useState(
    Array.isArray(coursesLeft)
      ? coursesLeft.reduce((acc, category) => {
          category.courses.forEach((course) => {
            acc[course.name] = {
              term: course.term || "",
              notes: course.notes || "",
              option: course.options ? course.selectedOption || "" : "",
            };
          });
          return acc;
        }, {})
      : {}
  );

  const handleTermChange = (courseName, term) => {
    setSelectedTerms((prev) => ({
      ...prev,
      [courseName]: { ...prev[courseName], term },
    }));
  };

  const handleNotesChange = (courseName, notes) => {
    setSelectedTerms((prev) => ({
      ...prev,
      [courseName]: { ...prev[courseName], notes },
    }));
  };

  const handleOptionChange = (courseName, option) => {
    setSelectedTerms((prev) => ({
      ...prev,
      [courseName]: { ...prev[courseName], option },
    }));
  };

  const handleSubmit = () => {
    console.log("Selected Terms, Notes, and Options:", selectedTerms);
  };

  return (
    <div style={containerStyles}>
      <h1>Remaining Courses</h1>
      {coursesLeft.map((category) => (
        <div key={category.category} style={categoryStyles}>
          <h2>{category.category}</h2>
          <div style={headerStyles}>
            <span style={courseHeaderStyles}>Course</span>
            <span style={termHeaderStyles}>Term</span>
            <span style={notesHeaderStyles}>Notes</span>
          </div>
          {category.courses.map((course) => (
            <div key={course.name} style={courseRowStyles}>
              <span style={courseNameStyles}>
                {course.name} {course.options && "(Elective)"}
              </span>
              {/* Dynamic dropdown for electives based on category */}
              {electiveOptions[course.name] ? (
                <div style={selectGroupStyles}>
                  <select
                    value={selectedTerms[course.name]?.option || ""}
                    onChange={(e) => handleOptionChange(course.name, e.target.value)}
                    style={selectCourseStyles}
                  >
                    <option value="">Select Course</option>
                    {electiveOptions[course.name].map((option) => (
                      <option key={option} value={option}>
                        {option}
                      </option>
                    ))}
                  </select>
                  <select
                    value={selectedTerms[course.name]?.term || ""}
                    onChange={(e) => handleTermChange(course.name, e.target.value)}
                    style={dropdownStyles}
                  >
                    <option value="">Select Term</option>
                    <option value="Fall 2024">Fall 2024</option>
                    <option value="Spring 2025">Spring 2025</option>
                    <option value="Fall 2025">Fall 2025</option>
                    <option value="Spring 2026">Spring 2026</option>
                  </select>
                </div>
              ) : (
                <select
                  value={selectedTerms[course.name]?.term || ""}
                  onChange={(e) => handleTermChange(course.name, e.target.value)}
                  style={dropdownStyles}
                >
                  <option value="">Select Term</option>
                  <option value="Fall 2024">Fall 2024</option>
                  <option value="Spring 2025">Spring 2025</option>
                  <option value="Fall 2025">Fall 2025</option>
                  <option value="Spring 2026">Spring 2026</option>
                </select>
              )}
              <input
                type="text"
                placeholder="Add notes here"
                value={selectedTerms[course.name]?.notes || ""}
                onChange={(e) => handleNotesChange(course.name, e.target.value)}
                style={notesInputStyles}
              />
            </div>
          ))}
        </div>
      ))}
      <button onClick={handleSubmit} style={buttonStyles}>
        Submit
      </button>
    </div>
  );
};

// Styles
const containerStyles = {
  padding: "20px",
  fontFamily: "Arial, sans-serif",
  maxWidth: "900px",
  margin: "0 auto",
};

const categoryStyles = {
  marginBottom: "20px",
};

const headerStyles = {
  display: "flex",
  justifyContent: "space-between",
  padding: "10px 0",
  borderBottom: "2px solid #ccc",
  fontWeight: "bold",
  marginBottom: "10px",
};

const courseRowStyles = {
  display: "flex",
  justifyContent: "space-between",
  alignItems: "center",
  marginBottom: "10px",
  gap: "10px", // Ensures proper spacing between all elements
};

const courseHeaderStyles = {
  flex: 3,
  textAlign: "left",
};

const termHeaderStyles = {
  flex: 1,
  textAlign: "center",
};

const notesHeaderStyles = {
  flex: 2,
  textAlign: "center",
};

const courseNameStyles = {
  flex: 3,
  textAlign: "left",
};

const selectGroupStyles = {
  display: "flex",
  flexDirection: "row",
  alignItems: "center",
  gap: "10px", // Adds spacing between the "Select Course" and "Select Term"
};

const selectCourseStyles = {
  width: "250px", // Fixed width for "Select Course" dropdown
  padding: "5px",
  borderRadius: "4px",
  border: "1px solid #ccc",
  height: "36px",
};

const dropdownStyles = {
  width: "150px", // Fixed width for "Select Term" dropdown
  padding: "5px",
  borderRadius: "4px",
  border: "1px solid #ccc",
  height: "36px",
};

const notesInputStyles = {
  flex: 2,
  padding: "5px",
  borderRadius: "4px",
  border: "1px solid #ccc",
  height: "36px",
};

const buttonStyles = {
  padding: "10px 20px",
  backgroundColor: "#007BFF",
  color: "#fff",
  border: "none",
  borderRadius: "4px",
  cursor: "pointer",
  marginTop: "20px",
};

export default CoursesLeft;
