const CoursesLeft = ({ coursesLeft = [] }) => {
    const [selectedTerms, setSelectedTerms] = useState(
      Array.isArray(coursesLeft)
        ? coursesLeft.reduce((acc, category) => {
            category.courses.forEach((course) => {
              acc[course.name] = {
                term: course.term || "",
                notes: course.notes || "",
                option: course.options ? course.selectedOption || "" : "",
              };
            });
            return acc;
          }, {})
        : {}
    );

    const handleTermChange = (courseName, term) => {
      setSelectedTerms((prev) => ({
        ...prev,
        [courseName]: { ...prev[courseName], term },
      }));
    };

    const handleNotesChange = (courseName, notes) => {
      setSelectedTerms((prev) => ({
        ...prev,
        [courseName]: { ...prev[courseName], notes },
      }));
    };

    const handleOptionChange = (courseName, option) => {
      setSelectedTerms((prev) => ({
        ...prev,
        [courseName]: { ...prev[courseName], option },
      }));
    };

    const handleSubmit = () => {
      console.log("Selected Terms, Notes, and Options:", selectedTerms);
    };

    return (
      <div
        style={{
          padding: "20px",
          maxWidth: "900px",
          margin: "0 auto",
          backgroundColor: "#FFFFFF", // White background
          borderRadius: "8px",
          boxShadow: "0px 4px 6px rgba(0, 0, 0, 0.1)",
          color: "#500000", // Maroon text
          fontFamily: "Arial, sans-serif",
        }}
      >
        <div
          style={{
            backgroundColor: "#500000", // Maroon background
            color: "#FFFFFF", // White text
            textAlign: "center",
            padding: "10px",
            borderRadius: "4px",
            fontWeight: "bold",
            fontSize: "24px",
          }}
        >
          Texas A&M Degree Plan
        </div>
        <h1 style={{ textAlign: "center", color: "#500000" }}>Remaining Courses</h1>
        {coursesLeft.map((category) => (
          <div key={category.category}>
            <div
              style={{
                backgroundColor: "#500000", // Maroon background
                color: "#FFFFFF", // White text
                padding: "10px",
                marginBottom: "10px",
                borderRadius: "4px",
              }}
            >
              <h2>{category.category}</h2>
            </div>
            {category.courses.map((course) => (
              <div
                key={course.name}
                style={{
                  display: "flex",
                  justifyContent: "space-between",
                  alignItems: "center",
                  padding: "10px",
                  borderBottom: "1px solid #EEEEEE",
                  marginBottom: "10px",
                }}
              >
                <span>{course.name} {course.options && "(Elective)"}</span>
                {course.options ? (
                  <div style={{ display: "flex", gap: "10px" }}>
                    <select
                      value={selectedTerms[course.name]?.option || ""}
                      onChange={(e) => handleOptionChange(course.name, e.target.value)}
                      style={{
                        padding: "8px",
                        borderRadius: "4px",
                        border: "1px solid #CCCCCC",
                        backgroundColor: "#FFFFFF",
                        color: "#500000",
                      }}
                    >
                      <option value="">Select Course</option>
                      {course.options.map((option) => (
                        <option key={option} value={option}>
                          {option}
                        </option>
                      ))}
                    </select>
                    <select
                      value={selectedTerms[course.name]?.term || ""}
                      onChange={(e) => handleTermChange(course.name, e.target.value)}
                      style={{
                        padding: "8px",
                        borderRadius: "4px",
                        border: "1px solid #CCCCCC",
                        backgroundColor: "#FFFFFF",
                        color: "#500000",
                      }}
                    >
                      <option value="">Select Term</option>
                      <option value="Fall 2024">Fall 2024</option>
                      <option value="Spring 2025">Spring 2025</option>
                      <option value="Fall 2025">Fall 2025</option>
                      <option value="Spring 2026">Spring 2026</option>
                    </select>
                  </div>
                ) : (
                  <select
                    value={selectedTerms[course.name]?.term || ""}
                    onChange={(e) => handleTermChange(course.name, e.target.value)}
                    style={{
                      padding: "8px",
                      borderRadius: "4px",
                      border: "1px solid #CCCCCC",
                      backgroundColor: "#FFFFFF",
                      color: "#500000",
                    }}
                  >
                    <option value="">Select Term</option>
                    <option value="Fall 2024">Fall 2024</option>
                    <option value="Spring 2025">Spring 2025</option>
                    <option value="Fall 2025">Fall 2025</option>
                    <option value="Spring 2026">Spring 2026</option>
                  </select>
                )}
                <input
                  type="text"
                  placeholder="Add notes here"
                  value={selectedTerms[course.name]?.notes || ""}
                  onChange={(e) => handleNotesChange(course.name, e.target.value)}
                  style={{
                    width: "100%",
                    padding: "8px",
                    borderRadius: "4px",
                    border: "1px solid #CCCCCC",
                    backgroundColor: "#FFFFFF",
                    color: "#500000",
                  }}
                />
              </div>
            ))}
          </div>
        ))}
        <button
          onClick={handleSubmit}
          style={{
            padding: "10px 20px",
            backgroundColor: "#500000",
            color: "#FFFFFF",
            border: "none",
            borderRadius: "4px",
            cursor: "pointer",
            marginTop: "20px",
          }}
        >
          Submit
        </button>
      </div>
    );
  };

  export default CoursesLeft;
